.login-container {
  width: 100%;
  height: 100%;
  overflow: auto;
  display: flex;
  flex-direction: column;
  justify-content: center;
  background-image: url('../../assets/login-img.png'), url('../../assets/login--bg.png');
  background-repeat: no-repeat, no-repeat;
  background-position: 0px, 0px;
  background-size: cover;
  position: fixed;
  background-color: #272a3a;
  .el-menu-demo{
    height: 60px;
    position: fixed;
    top: 0;
    width: 100%;
    z-index: 111;
  }
  .el-menu.el-menu--horizontal{
    border-bottom: none;
    .top-img{
      float: left;
      width: 260px;
      margin: 13px 10px 10px 31px;
    }
    .top-btn{
      float: right;
      margin-right: 10%;
      margin-top: 10px;
      color: #189fff;
      background-color: #fff
    }
  }
  .el-menu--horizontal > .el-menu-item.is-active{
    border-bottom: none
  }
  .box-card {
    z-index: 110;
    width: 400px;
    position: absolute;
    right: 10%;
    top: 15%;
    .el-card__body{
      padding: 16px 30px 30px;
    }
  }
  .el-input {
    display: inline-block;
    width: 100%;
    padding: 0;
  }
  .el-form-item{
    margin-bottom: 8px;
    .el-form-item__label{
      padding: 0;
      font-weight: 400;
    }
  }
  .el-tabs__item{
    font-size: 17px
  }
  .btn-width{
    width: 100%;
    margin-top: 20px;
  }
  .captcha-input{
    width: 50%;
    position: relative;
  }
  .captcha-img{
    position: absolute;
    right: 0;
    width: 40%;
    height: 36px;
  }
  .captcha-msg{
    width: 40px;
  }
}
